import { red } from '@material-ui/core/colors';
import { createMuiTheme } from '@material-ui/core/styles';

// A custom theme for this app
const theme = createMuiTheme({
  palette: {
   
    primary: {
        light: '#757ce8',
        main: '#3f50b5',
        dark: '#002884',
        contrastText: '#fff',
      },
    secondary: 'lightBlue',
    error: {
      main: red.A400,
    },
    background: {
        main: 'red',
        light: 'blue'
    },
  },
  root: {
    flexGrow: 1,
  },
  paper: {
    margin: 'auto',
    maxWidth: 500,
  },
  image: {
    width: 128,
    height: 128,
  },
  img: {
    margin: 'auto',
    display: 'block',
    maxWidth: '100%',
    maxHeight: '100%',
  },
  flag: {
      display:'inline-block',
        marginRight: 30,
    },
});

export default theme;
