import { useState, useEffect } from 'react'
import useStats from "../src/useStats";
import Typography from '@material-ui/core/Typography';


export default function Last({url}) {
    const {stats, loading, error} = useStats(url);
    const options = {
        year: "numeric",
        month:"long",
        day:"numeric"
    }
    if (loading) return <p></p>;
    if (error) return <Typography variant="h6" component="h6">  Last Update: No Data</Typography>;
    return (
        <Typography variant="h6" component="h6" color="primary.light">
            Last Update: { (new Date(stats.lastUpdate)).toLocaleDateString('en-US', options) }
        </Typography>
    );
}
