import { useState, useEffect } from 'react'
import useStats from "../src/useStats";
import Stats from "../components/Stats";
import { makeStyles } from '@material-ui/core/styles';
import { ThemeProvider } from '@material-ui/core/styles';
import theme from '../src/theme';
import FormControl from '@material-ui/core/FormControl';
import Select from '@material-ui/core/Select';



const useStyles = makeStyles(theme => ({
    formControl: {
      margin: theme.spacing(1),
      minWidth: 120,
    },
    selectEmpty: {
      marginTop: theme.spacing(2),
    },
}));

export default function CountrySelector() {
    const classes = useStyles();
    const {stats: countries, loading, error} = useStats('https://covid19.mathdro.id/api/countries');
    const [selectedCountry, setSelectedCountry] = useState('MX');
    console.log(countries);
    if (loading) return <p>Loading...</p>;
    if (loading) return <p>Loading...</p>;
    if (error) return <p>Error...</p>;
    return (
        <Grid item xs={12}>
            <FormControl className={classes.formControl}>
                <Select labelId="demo-simple-select-helper-label"  id="demo-simple-select-helper"  onChange={(e) => {setSelectedCountry(e.target.value) }}>
                    {Object.entries(countries.countries).map(([country, code]) => {
                        return (
                            <option selected={selectedCountry === code} key={code} value={countries.iso3[code]}>
                                {country}
                            </option>
                        )
                    })}
                </Select>
                <Stats url={`https://covid19.mathdro.id/api/countries/${selectedCountry}`}> </Stats>
            </FormControl>
        </Grid>
    );
}
