import { useState, useEffect } from 'react'
import useStats from "../src/useStats";
import Typography from '@material-ui/core/Typography';


export default function Last({url}) {
    const {stats, loading, error} = useStats(url);
    console.log(stats);
    if (loading) return <p>Loading...</p>;
    if (error) return <Typography variant="h6" component="h6" gutterBottom>No info</Typography>;
    return (
        <Typography variant="h6" component="h6" gutterBottom>
            Last Update: { (new Date(stats.lastUpdate)).toLocaleDateString('en-US', options) }
        </Typography>
    );
}
