import { useEffect } from "react"

function useStats() {
    const [stats, setStats] = useState();
    useEffect(() => {
        async function fetchData() {
            console.log('Fetching Data');
            const data = await fetch('https://covid19.mathdro.id/api')
            .then(res => res.json());
            setStats(data);
        }
        fetchData();
    }, []);
    return stats;
}

function stats() {
    const stats = useStats();
    if(!stats) return <p>Loading...</p>
    console.log(stats);
    return (
        <div>
            <div>
                <h3>Confirmados</h3>
                <p>{stats.confirmed.value}</p>
            </div>
            <div>
                <h3>Muertos</h3>
                <p>{stats.deaths.value}</p>
            </div>
            <div>
                <h3>Curados</h3>
                <p>{stats.recovered.value}</p>
            </div>
        </div>
    );
}


export default function Index() {
    return <h1>Coronavirus</h1>
}
